putFile = null;\nBufferedReader _reader = null;\n\npublic boolean openFile(String fileName) {\n    _inputFile = new File(fileName);\n    try {\n        _reader = new BufferedReader(new FileReader(_inputFile));\n    } catch (IOException e) {\n        e.printStackTrace();\n        return false;\n    }\n    return true;\n}\n\npublic void OpenWriteFile(String fileName) throws IOException {\n    _outputFile = new FileWriter(fileName);\n}\n\npublic void WriteLineToFile(String varName) throws IOException {\n    varName+=\"\\r\\n\";\n    _outputFile.write(varName);\n}\n\npublic void CloseOutputFile() throws IOException {\n    _outputFile.close();\n}\n\npublic void ConvertFile(String fileName,String outputFile) throws IOException {\n    OpenWriteFile(outputFile);\n    if (openFile(fileName)) {\n        while (true) {\n            String tempString = _reader.readLine();\n            if (tempString != null) {\n                String outString = Convert(tempString);\n                WriteLineToFile(outString);\n            } else {\n                _reader.close();\n                break;\n            }\n\n        }\n    }\n    CloseOutputFile();\n}\n\npublic String Convert(String strVarName) {\n    String retString = new String(\"Error!\");\n    if (strVarName.length() == 0) {\n        return retString;\n    }\n    if (strVarName.length() &gt; 100) {\n        return retString;\n    }\n\n    if (isAllSmallLetters(strVarName)) {\n        retString = strVarName;\n    } else if (isJavaVarName(strVarName)) {\n        retString = ConvertJavaToCpp(strVarName);\n    } else if (isCplusplusVarName(strVarName)) {\n        retString = ConvertCppToJava(strVarName);\n    } else {\n        retString = \"Error!\";\n    }\n    return retString;\n}\n\nboolean isLowerChar(char c) {\n    return c &gt;= \'a\' &amp;&amp; c &lt;= \'z\';\n}\n\nboolean isUpperChar(char c) {\n    return c &gt;= \'A\' &amp;&amp; c &lt;= \'Z\';\n}\n\nboolean isAllSmallLetters(String name) {\n    int i = 0;\n    while (i &lt; name.length()) {\n        char c = name.charAt(i);\n        if (!isLowerChar(c)) {\n            return false;\n        }\n        ++i;\n    }\n    return true;\n}\n\npublic String ConvertCppToJava(String name) {\n    int i = 0;\n    String outString = new String();\n    while (i &lt; name.length()) {\n        char c = name.charAt(i);\n        if (c == \'_\') {\n            i++;\n            c = name.charAt(i);\n            outString += (char) (c - \'a\' + \'A\');\n        } else {\n            outString += c;\n        }\n        ++i;\n    }\n    return outString;\n}\n\npublic String ConvertJavaToCpp(String name) {\n    int i = 0;\n    String outString = new String();\n    while (i &lt; name.length()) {\n        char c = name.charAt(i);\n        if (isUpperChar(c)) {\n            outString += \'_\';\n            outString += (char) (c - \'A\' + \'a\');\n        } else {\n            outString += c;\n        }\n        ++i;\n    }\n    return outString;\n}\n\npublic boolean isCplusplusVarName(String name) {\n    int i = 0;\n    int state = 0;\n    if (name.length() == 0) return false;\n\n    if (!isLowerChar(name.charAt(0))) {\n        return false;\n    }\n    while (true) {\n        if (i &gt;= name.length()) {\n            if (state == 2) {\n                return false;\n            }\n            break;\n        }\n        char c = name.charAt(i);\n        switch (state) {\n            case 0: {\n                if (isLowerChar(c)) {\n                    state = 1;\n                }\n                break;\n            }\n            case 1: {\n                if (isUpperChar(c)) {\n                    return false;\n                } else if (c == \'_\') {\n                    state = 2;\n                }\n                break;\n            }\n            case 2: {\n                if (isUpperChar(c) || c == \'_\') {\n                    return false;\n                } else {\n                    state = 0;\n                }\n                break;\n            }\n\n        }\n        ++i;\n    }\n    return true;\n}\n\n//\npublic boolean isJavaVarName(String name) {\n    int i = 0;\n    int state = 0;\n    if (name.length() == 0) return false;\n\n    if (!isLowerChar(name.charAt(0))) {\n        return false;\n    }\n    while (true) {\n        if (i &gt;= name.length()) {\n            break;\n        }\n        char c = name.charAt(i);\n        switch (state) {\n            case 0: {\n                if (isLowerChar(c)) {\n                    state = 1;\n                }\n                break;\n            }\n            case 1: {\n                if (isUpperChar(c)) {\n                    state = 2;\n                } else if (c == \'_\') {\n                    return false;\n                }\n                break;\n            }\n            case 2: {\n                if (isUpperChar(c)) {\n                    System.out.println(\"连续大写\");\n                    return false;\n                } else {\n                    state = 0;\n                }\n                break;\n            }\n\n        }\n        ++i;\n    }\n    return true;\n}\n</code></pre>\n\n<p>}</p>\n\n<pre><code>\n</code></pre>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1615,607,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p><a href=\"http://www.blogbus.com/giggs1123-logs/69070287.html\">http://www.blogbus.com/giggs1123-logs/69070287.html</a></p>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1616,608,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p><a href=\"http://www.2cto.com/kf/201309/246272.html\">http://www.2cto.com/kf/201309/246272.html</a></p>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1617,609,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p><a href=\"http://www.jb51.net/article/49453.htm\">http://www.jb51.net/article/49453.htm</a></p>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1618,609,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p>首先要有质数数据，之后在质数数据中去寻找求和能相等的几个质数。<br>\n这种求解当然有多种方法，首先想到的方法：取第一个质数，减去输入值，再寻找是否包含在质数中。</p>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1619,609,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p>首先用筛法给素数打表，然后从3枚举到n/2，如果x=y+z并且y和z都是素数就说明满足条件，打印即可。其实歌德巴赫猜想也可以这样说：**任何一个&gt;=6之偶数，都可以表示成两个奇素数之和。**证明：因为x本身是偶数，如果x是2，y就是大于等于4的偶数，y一定不会是素数，所以y和z必定是奇素数。<br>\n附上此题的证明代码：<a href=\"http://paste.ubuntu.com/23832105/\">哥德巴赫猜想证明代码</a><br>\n你这个问题还是比较有趣的，是不是某个OJ的问题？</p>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1620,610,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p>用cmp 指令<br><br>\nmov al,5<br>\nmov bl,5<br>\ncmp al,bl</p>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1621,610,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p>没写过汇编也可以解决这个问题，比如写个函数<br>\nbool cmpNumber(int value , int MinValue,int MaxValue)<br>\n{<br>\n    return value &gt;MinValue &amp;&amp; value&lt;MaxValue;<br>\n}<br>\n然后如果用vs 的话，可以生成汇编代码，然后把汇编代码复制上就可以了。</p>\n\n<p>如果是要学点儿知识的话：</p>\n\n<p>无符号的比较是这样：<br>\n这个函数就是 如果 x&gt;minValue &amp;&amp;　x &lt;maxValue 返回 1 否则返回 0<br>\n这是用masm实现<br>\ncmpNumber proc  x  : DWORD , minValue : DWORD ,maxValue : DWORD<br>\n；默认返回值<br>\n mov eax , 0<br>\n    MOV edx , x<br>\n    cmp edx , minValue<br>\n    ja $f<br>\n    jmp ret_jmp<br>\n    $$:<br>\n  cmp edx , maxValue<br>\n    jb $f<br>\n    jmp ret_jmp<br>\n    $$:<br>\n  mov eax ,1<br>\n    ret_jmp:<br>\n    ret<br>\n    cmpNumber endp</p>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1622,610,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <pre><code> je 相等跳转\njne 不等跳转\njg 大于跳转\njl 小于跳转\njge 大于等于跳转\njle 小于等于跳转\n</code></pre>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1623,610,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p>如果是有符号的比较，则使用 jg 表示高于 jl 表示低于</p>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1624,610,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p>你做两次判断不就好了。</p>\n  </div>',1,1,0,'2017-02-12 12:12:49','2017-02-12 12:12:49'),(1625,611,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p>oracle 一般用plsql developer这个软件，挺好用的</p>\n  </div>',1,1,0,'2017-02-12 12:12:50','2017-02-12 12:12:50'),(1626,612,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p>对应的插件应该没有下载<br>\n运行不抱错可以不管的</p>\n  </div>',1,1,0,'2017-02-12 12:12:50','2017-02-12 12:12:50'),(1627,613,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p>最好把页面源码发过来，看图片不好看</p>\n  </div>',1,1,0,'2017-02-12 12:12:50','2017-02-12 12:12:50'),(1628,614,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666;line-height: 24px;word-break: break-all;word-wrap: break-word;\">\n    <p><a href=\"http://www.cnblogs.com/vongang/archive/2011/09/27/2193375.html\">http://www.cnblogs.com/vongang/archive/2011/09/27/2193375.html</a></p>\n  </div>',1,1,0,'2017-02-12 12:12:50','2017-02-12 12:12:50'),(1629,615,'<div style=\"margin: 0 20px 10px;font-size: 14px;color: #666